  // ç®€æ´çº¿æ€§å›¾è¡¨ç»„ä»¶
  const MiniLineChart = ({ isPositive }: { isPositive: boolean }) => {
    const generateLineData = () => {
      const points = []
      let baseValue = 50

      for (let i = 0; i < 15; i++) {
        const trend = isPositive ? 0.5 : -0.5
        const noise = (Math.random() - 0.5) * 8
