    opacity: 0.9;
  }
  100% {
    transform: translateX(0);
    opacity: 1;
  }
}

.animate-slide {
  animation: slide-horizontal 0.3s ease-out;
}

.sort-container .animate-slide button {
  animation: slide-horizontal 0.3s ease-out;
}

