  getAvailableBalanceForCurrency: (currency: string) => number
}

export interface WithdrawalFormData {
  recipientName: string
  recipientAccount: string
  recipientBank: string
  amount: string
  paymentMethod: string
  purpose: string
  currency: string
}

interface FormErrors {
  [key: string]: string
}

export function WithdrawalModal({ 
  isOpen, 
  onClose, 
  onSubmit, 
  currentCurrency, 
  getAvailableBalanceForCurrency 
}: WithdrawalModalProps) {
  const { theme } = useTheme()
  const isDark = theme === "dark"

  // è¡¨å•çŠ¶æ€
  const [formData, setFormData] = useState<WithdrawalFormData>({
    recipientName: "",
    recipientAccount: "",
    recipientBank: "",
    amount: "",
    paymentMethod: "é“¶è¡Œè½¬è´¦",
    purpose: "",
    currency: currentCurrency
  })

  // éªŒè¯é”™è¯¯çŠ¶æ€
  const [errors, setErrors] = useState<FormErrors>({})

  // æäº¤çŠ¶æ€
  const [isSubmitting, setIsSubmitting] = useState(false)

  // å½“è´§å¸å‘ç”Ÿå˜åŒ–æ—¶æ›´æ–°è¡¨å•
  useEffect(() => {
    setFormData(prev => ({ ...prev, currency: currentCurrency }))
  }, [currentCurrency])

  // è·å–å½“å‰é€‰æ‹©è´§å¸çš„å¯ç”¨ä½™é¢
  const getCurrentBalance = () => {
    return getAvailableBalanceForCurrency(formData.currency)
  }

  // é‡ç½®è¡¨å•
  const resetForm = () => {
    setFormData({
      recipientName: "",
      recipientAccount: "",
      recipientBank: "",
      amount: "",
      paymentMethod: "é“¶è¡Œè½¬è´¦",
      purpose: "",
      currency: currentCurrency
    })
    setErrors({})
    setIsSubmitting(false)
  }

  // å¤„ç†è¡¨å•å­—æ®µå˜åŒ–
  const handleFieldChange = (field: keyof WithdrawalFormData, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }))

    // æ¸…é™¤ç›¸å…³å­—æ®µçš„é”™è¯¯
    if (errors[field]) {
      setErrors(prev => {
        const newErrors = { ...prev }
        delete newErrors[field]
        return newErrors
      })
    }
  }

  // è¡¨å•éªŒè¯
  const validateForm = (): boolean => {
    const newErrors: FormErrors = {}

    // æ”¶æ¬¾äººå§“åéªŒè¯
    if (!formData.recipientName.trim()) {
      newErrors.recipientName = "è¯·è¾“å…¥æ”¶æ¬¾äººå§“å"
    } else if (formData.recipientName.trim().length < 2) {
      newErrors.recipientName = "æ”¶æ¬¾äººå§“åè‡³å°‘éœ€è¦2ä¸ªå­—ç¬¦"
    }

    // æ”¶æ¬¾è´¦æˆ·éªŒè¯
    if (!formData.recipientAccount.trim()) {
      newErrors.recipientAccount = "è¯·è¾“å…¥æ”¶æ¬¾è´¦æˆ·"
    } else if (formData.recipientAccount.trim().length < 6) {
      newErrors.recipientAccount = "æ”¶æ¬¾è´¦æˆ·è‡³å°‘éœ€è¦6ä¸ªå­—ç¬¦"
    }

    // æ”¶æ¬¾é“¶è¡ŒéªŒè¯
    if (!formData.recipientBank.trim()) {
      newErrors.recipientBank = "è¯·è¾“å…¥æ”¶æ¬¾é“¶è¡Œ"
    }

    // é‡‘é¢éªŒè¯
    const currentBalance = getCurrentBalance()
    if (!formData.amount) {
      newErrors.amount = "è¯·è¾“å…¥ä»£ä»˜é‡‘é¢"
    } else {
      const amount = parseFloat(formData.amount)
      if (isNaN(amount) || amount <= 0) {
        newErrors.amount = "è¯·è¾“å…¥æœ‰æ•ˆçš„ä»£ä»˜é‡‘é¢"
      } else if (amount > currentBalance) {
        newErrors.amount = `ä»£ä»˜é‡‘é¢ä¸èƒ½è¶…è¿‡ä»£ä»˜å¤‡ä½£é‡‘ä½™é¢ ${formatCurrency(currentBalance, formData.currency)}`
      } else if (amount < 10) {
