      }
    }

    // ç”¨é€”è¯´æ˜éªŒè¯
    if (!formData.purpose.trim()) {
      newErrors.purpose = "è¯·è¾“å…¥ç”¨é€”è¯´æ˜"
    } else if (formData.purpose.trim().length < 10) {
