    } else if (formData.purpose.trim().length > 200) {
      newErrors.purpose = "ç”¨é€”è¯´æ˜ä¸èƒ½è¶…è¿‡200ä¸ªå­—ç¬¦"
    }

    setErrors(newErrors)
