'use client'

import React from 'react'
import { Check } from 'lucide-react'

interface Step {
  id: string
  label: string
  status: 'completed' | 'current' | 'pending' | 'dispute'
}

interface TransactionProgressProps {
  steps: Step[]
  className?: string
}

export default function TransactionProgress({ steps, className = '' }: TransactionProgressProps) {
  // è®¡ç®—å·²å®Œæˆçš„æ­¥éª¤æ•°é‡
  const completedCount = steps.filter(step => step.status === 'completed').length
  const currentIndex = steps.findIndex(step => step.status === 'current')

  // è®¡ç®—è¿›åº¦ç™¾åˆ†æ¯” - åŸºäºå·²å®Œæˆçš„æ­¥éª¤
  const progressPercentage = currentIndex === -1 
    ? (completedCount / (steps.length - 1)) * 100
    : ((completedCount + 0.5) / (steps.length - 1)) * 100

  return (
    <div className={`w-full ${className}`}>
      <div className="relative py-6 px-4">
        {/* ç»Ÿä¸€çš„èŠ‚ç‚¹å®¹å™¨ - æ ¹æ®å±å¹•å®½åº¦è‡ªé€‚åº”é—´è· */}
        <div className="flex items-start justify-between w-full">
          {steps.map((step, index) => (
            <div key={step.id} className="flex flex-col items-center relative">
              {/* æ°”æ³¡æ ·å¼æ ‡ç­¾ */}
              <div className={`
                relative px-3 py-2 rounded-lg text-sm font-medium text-center
                whitespace-nowrap shadow-sm mb-2
                ${step.status === 'completed' 
                  ? 'bg-green-100 text-green-800 border border-green-200' 
                  : step.status === 'current'
                  ? 'bg-yellow-100 text-yellow-800 border border-yellow-200'
                  : step.status === 'dispute'
                  ? 'bg-red-100 text-red-800 border border-red-200'
                  : 'bg-gray-100 text-gray-600 border border-gray-200'
                }
              `}>
                {step.label}
                {/* æ°”æ³¡å°–è§’ */}
                <div className={`
                  absolute top-full left-1/2 transform -translate-x-1/2 
                  w-0 h-0 border-l-[6px] border-r-[6px] border-t-[6px] border-l-transparent border-r-transparent
                  ${step.status === 'completed' 
                    ? 'border-t-green-200' 
                    : step.status === 'current'
                    ? 'border-t-yellow-200'
                    : step.status === 'dispute'
                    ? 'border-t-red-200'
                    : 'border-t-gray-200'
                  }
                `} />
              </div>

              {/* åœ†çƒèŠ‚ç‚¹ - ç›´æ¥åœ¨æ°”æ³¡ä¸‹æ–¹ */}
              <div 
                className={`
                  w-6 h-6 rounded-full flex items-center justify-center
                  border-2 border-white shadow-md relative z-10
                  ${step.status === 'completed' 
                    ? 'bg-green-500' 
                    : step.status === 'current'
                    ? 'bg-yellow-400'
                    : step.status === 'dispute'
                    ? 'bg-red-500'
                    : 'bg-gray-400'
                  }
                `}
              >
                {step.status === 'completed' && (
                  <Check className="w-3 h-3 text-white" strokeWidth={3} />
                )}
                {step.status === 'current' && (
                  <div className="w-2 h-2 bg-white rounded-full" />
                )}
              </div>
            </div>
          ))}
        </div>

        {/* è¿æ¥çº¿ - ä½¿ç”¨flexå¸ƒå±€è‡ªåŠ¨è®¡ç®—ä½ç½® */}
        {steps.length > 1 && (
          <div className="absolute flex items-center" style={{ 
            bottom: '31px',
            left: '0',
            right: '0',
            paddingLeft: 'calc(2rem + 12px)',
            paddingRight: 'calc(2rem + 12px)'
          }}>
            <div className="flex-1 h-1 bg-gray-200" />
          </div>
        )}

        {/* è¿›åº¦çº¿ - ä½¿ç”¨flexå¸ƒå±€è‡ªåŠ¨è®¡ç®—ä½ç½® */}
        {steps.length > 1 && (
          <div className="absolute flex items-center" style={{ 
            bottom: '31px',
            left: '0',
            right: '0',
            paddingLeft: 'calc(2rem + 12px)',
            paddingRight: 'calc(2rem + 12px)'
          }}>
            <div 
              className="h-1 bg-green-500 transition-all duration-500 ease-out"
              style={{ 
                width: `${Math.min(progressPercentage, 100)}%`
              }}
            />
          </div>
        )}
      </div>
    </div>
  )
}

