"use client"

import React, { useState } from "react"
import { X, Lock, Mail, Eye, EyeOff, CheckCircle, AlertCircle } from "lucide-react"

interface ChangePasswordModalProps {
  isOpen: boolean
  onClose: () => void
}

export default function ChangePasswordModal({ isOpen, onClose }: ChangePasswordModalProps) {
  const [currentPassword, setCurrentPassword] = useState("")
  const [newPassword, setNewPassword] = useState("")
  const [confirmPassword, setConfirmPassword] = useState("")
  const [verificationCode, setVerificationCode] = useState("")
  const [showCurrentPassword, setShowCurrentPassword] = useState(false)
  const [showNewPassword, setShowNewPassword] = useState(false)
  const [showConfirmPassword, setShowConfirmPassword] = useState(false)
  const [isLoading, setIsLoading] = useState(false)
  const [isSendingCode, setIsSendingCode] = useState(false)
  const [codeSent, setCodeSent] = useState(false)
  const [countdown, setCountdown] = useState(0)
  const [error, setError] = useState("")
  const [success, setSuccess] = useState(false)
  const [adminEmail, setAdminEmail] = useState("")

  React.useEffect(() => {
    if (isOpen && typeof window !== 'undefined') {
      const email = localStorage.getItem("adminEmail") || "admin@bedao.com"
      setAdminEmail(email)
    }
  }, [isOpen])

  if (!isOpen) return null

  const handleSendCode = async () => {
    setIsSendingCode(true)
    setError("")

    setTimeout(() => {
      setCodeSent(true)
      setIsSendingCode(false)
      setCountdown(60)

      const timer = setInterval(() => {
        setCountdown((prev) => {
          if (prev <= 1) {
            clearInterval(timer)
            return 0
          }
          return prev - 1
        })
      }, 1000)
    }, 1000)
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    setError("")

    if (!currentPassword || !newPassword || !confirmPassword || !verificationCode) {
      setError("è¯·å¡«å†™æ‰€æœ‰å¿…å¡«é¡¹")
      return
    }

    if (newPassword !== confirmPassword) {
      setError("ä¸¤æ¬¡è¾“å…¥çš„æ–°å¯†ç ä¸ä¸€è‡´")
      return
    }

    if (newPassword.length < 6) {
      setError("æ–°å¯†ç é•¿åº¦è‡³å°‘ä¸º6ä½")
      return
    }

    if (verificationCode.length !== 6) {
      setError("éªŒè¯ç åº”ä¸º6ä½æ•°å­—")
      return
    }

    setIsLoading(true)

    setTimeout(() => {
      setSuccess(true)
      setIsLoading(false)

      setTimeout(() => {
        onClose()
        setCurrentPassword("")
        setNewPassword("")
        setConfirmPassword("")
