} from "@/components/ui/dropdown-menu"

interface AdminTopNavProps {
  currentModule: string
  onModuleChange: (module: string) => void
  onToggleSidebar?: () => void
}

const modules = [
  { id: "permissions", label: "æƒé™", icon: Shield },
  { id: "orders", label: "è´¢åŠ¡", icon: Wallet },
  { id: "system", label: "å®¡æ ¸", icon: FileCheck },
  { id: "marketing", label: "è¿è¥", icon: Activity },
  { id: "users", label: "ç”¨æˆ·", icon: Users },
  { id: "crypto", label: "Crypto", icon: Repeat },
  { id: "fiat", label: "æ³•å¸", icon: Store },
  { id: "commission", label: "ä½£é‡‘", icon: Percent },
  { id: "spot", label: "ç°è´§", icon: TrendingUp },
  { id: "futures", label: "åˆçº¦", icon: TrendingUp },
  { id: "copytrade", label: "è·Ÿå•", icon: Copy },
  { id: "options", label: "æœŸæƒ", icon: LineChart },
  { id: "c2c", label: "C2C", icon: DollarSign },
  { id: "escrow", label: "æ‹…ä¿", icon: Shield },
  { id: "finance", label: "ç†è´¢", icon: PiggyBank },
  { id: "ucard", label: "Uå¡", icon: CreditCard },
  { id: "social", label: "ç¤¾äº¤", icon: Share2 },
  { id: "im", label: "IM", icon: MessageSquare },
]

export default function AdminTopNav({ currentModule, onModuleChange, onToggleSidebar }: AdminTopNavProps) {
  const { theme, setTheme } = useTheme()
  const { logout, userInfo } = useAdmin()
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false)
  const [mounted, setMounted] = useState(false)
  const [showLeftScroll, setShowLeftScroll] = useState(false)
  const [showRightScroll, setShowRightScroll] = useState(false)
  const scrollContainerRef = useRef<HTMLDivElement>(null)

  useEffect(() => {
    setMounted(true)
  }, [])

  const handleModuleClick = (moduleId: string) => {
    onModuleChange(moduleId)
    setMobileMenuOpen(false)
  }

  const checkScrollPosition = () => {
    if (!scrollContainerRef.current) return

    const { scrollLeft, scrollWidth, clientWidth } = scrollContainerRef.current
    setShowLeftScroll(scrollLeft > 0)
    setShowRightScroll(scrollLeft < scrollWidth - clientWidth - 10)
  }

  const scroll = (direction: 'left' | 'right') => {
    if (!scrollContainerRef.current) return

    const scrollAmount = 200
    const newScrollLeft = scrollContainerRef.current.scrollLeft + (direction === 'left' ? -scrollAmount : scrollAmount)

    scrollContainerRef.current.scrollTo({
      left: newScrollLeft,
      behavior: 'smooth'
    })
  }

  useEffect(() => {
    checkScrollPosition()
    const container = scrollContainerRef.current
    if (container) {
      // æ»šåŠ¨äº‹ä»¶ç›‘å¬
      container.addEventListener('scroll', checkScrollPosition)
      window.addEventListener('resize', checkScrollPosition)

      // é¼ æ ‡æ»šè½®æ¨ªå‘æ»šåŠ¨
      const handleWheel = (e: WheelEvent) => {
        if (e.deltaY !== 0) {
          e.preventDefault()
          container.scrollLeft += e.deltaY
          checkScrollPosition()
        }
      }

      container.addEventListener('wheel', handleWheel, { passive: false })

      return () => {
        container.removeEventListener('scroll', checkScrollPosition)
        window.removeEventListener('resize', checkScrollPosition)
        container.removeEventListener('wheel', handleWheel)
      }
    }
  }, [])

  return (
    <div className={`h-14 ${theme === 'dark' ? 'bg-gray-800 border-gray-700' : 'bg-white border-gray-200'} border-b flex items-center justify-between px-4`}>
      {/* æ¡Œé¢ç«¯ï¼šä¸€çº§èœå•å¯¼èˆªï¼ˆå¸¦æ»šåŠ¨ç®­å¤´ï¼‰ */}
      <div className="hidden md:flex flex-1 items-center gap-2">
        {/* å·¦ä¾§æ»šåŠ¨æŒ‰é’® */}
        <button
          onClick={() => scroll('left')}
          disabled={!showLeftScroll}
          aria-label="å‘å·¦æ»šåŠ¨"
          className={`p-1.5 rounded-md transition-colors flex-shrink-0 ${
            showLeftScroll
              ? theme === 'dark'
                ? 'bg-gray-700 text-gray-300 hover:bg-gray-600 hover:text-white'
                : 'bg-gray-100 text-gray-600 hover:bg-gray-200 hover:text-gray-900'
              : theme === 'dark'
                ? 'bg-gray-800 text-gray-600 cursor-not-allowed'
                : 'bg-gray-50 text-gray-300 cursor-not-allowed'
          }`}
        >
          <ChevronLeft className="w-4 h-4" />
          <span className="sr-only">å‘å·¦æ»šåŠ¨</span>
        </button>

        {/* æ»šåŠ¨å®¹å™¨ */}
        <div 
          ref={scrollContainerRef}
          className="flex-1 flex items-center gap-1 overflow-x-auto scrollbar-hide relative"
          style={{ maxWidth: 'calc(100vw - 250px)' }}
        >
          {modules.map((module) => {
            const Icon = module.icon
            const isActive = currentModule === module.id

            return (
              <button
                key={module.id}
                onClick={() => handleModuleClick(module.id)}
                className={`
                  flex items-center gap-2 px-3 py-1.5 rounded-md text-sm font-medium whitespace-nowrap transition-colors flex-shrink-0
                  ${isActive 
                    ? 'bg-custom-green text-white'
                    : theme === 'dark'
                      ? 'text-gray-300 hover:bg-gray-700 hover:text-white'
                      : 'text-gray-600 hover:bg-gray-100 hover:text-gray-900'
                  }
                `}
              >
                <Icon className="w-4 h-4" />
                <span>{module.label}</span>
              </button>
            )
          })}
        </div>

        {/* å³ä¾§æ»šåŠ¨æŒ‰é’® */}
        <button
          onClick={() => scroll('right')}
          disabled={!showRightScroll}
          aria-label="å‘å³æ»šåŠ¨"
          className={`p-1.5 rounded-md transition-colors flex-shrink-0 ${
            showRightScroll
              ? theme === 'dark'
                ? 'bg-gray-700 text-gray-300 hover:bg-gray-600 hover:text-white'
                : 'bg-gray-100 text-gray-600 hover:bg-gray-200 hover:text-gray-900'
              : theme === 'dark'
                ? 'bg-gray-800 text-gray-600 cursor-not-allowed'
                : 'bg-gray-50 text-gray-300 cursor-not-allowed'
          }`}
        >
          <ChevronRight className="w-4 h-4" />
          <span className="sr-only">å‘å³æ»šåŠ¨</span>
        </button>
      </div>

      {/* ç§»åŠ¨ç«¯ï¼šæ±‰å ¡èœå•æŒ‰é’®ï¼ˆå·¦ä¾§ï¼‰ */}
