    setSearchTerm(searchInput)
  }

  const handleReset = () => {
    setSearchInput("")
    setSearchTerm("")
  }

  return {
    searchInput,
    setSearchInput,
    searchTerm,
    handleSearch,
    handleReset,
  }
}

